from pybricks.hubs import PrimeHub
from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import wait

# Set up all devices.
prime_hub = PrimeHub()
right = Motor(Port.A, Direction.COUNTERCLOCKWISE)
left = Motor(Port.E, Direction.CLOCKWISE)
drive_base = DriveBase(left, right, 87, 100)
left_attachment = Motor(Port.F, Direction.CLOCKWISE)
right_attachment = Motor(Port.B, Direction.CLOCKWISE)


# The main program starts here.
drive_base.settings(straight_speed=500)
drive_base.settings(straight_acceleration=400)
drive_base.use_gyro(False)
drive_base.straight(-50, then=Stop.BRAKE)
drive_base.straight(780, then=Stop.BRAKE)
drive_base.use_gyro(False)
wait(200)
left_attachment.run_angle(2000, -800)
drive_base.straight(-250, then=Stop.BRAKE)
drive_base.settings(turn_rate=1000)
wait(1000)
drive_base.turn(90, then=Stop.BRAKE)
drive_base.settings(turn_rate=100)
drive_base.settings(turn_acceleration=200)
drive_base.straight(-100, then=Stop.BRAKE)
wait(200)
drive_base.turn(120, then=Stop.BRAKE)
drive_base.straight(500, then=Stop.BRAKE)
left_attachment.control.limits(acceleration=1000)
