from pybricks.hubs import PrimeHub
from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase

# Set up all devices.
prime_hub = PrimeHub()
left = Motor(Port.A, Direction.COUNTERCLOCKWISE)
right = Motor(Port.E, Direction.CLOCKWISE)
drive_base = DriveBase(left, right, 87.3, 110.5)
left_motor = Motor(Port.B, Direction.CLOCKWISE)


# The main program starts here.
drive_base.use_gyro(True)
left_motor.reset_angle(0)
drive_base.straight(430)
left_motor.run_target(500, 260)
drive_base.straight(-200)
left_motor.run_angle(500, -250)
drive_base.use_gyro(True)
drive_base.settings(straight_speed=200)
drive_base.turn(3.67)
drive_base.straight(367)
drive_base.use_gyro(False)
drive_base.settings(straight_speed=600)
drive_base.straight(-600)
