from pybricks.hubs import PrimeHub
from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import multitask, run_task, wait

# Set up all devices.
prime_hub = PrimeHub()
left = Motor(Port.A, Direction.COUNTERCLOCKWISE)
right = Motor(Port.E, Direction.CLOCKWISE)
drive_base = DriveBase(left, right, 80, 100)
right_motor = Motor(Port.B, Direction.COUNTERCLOCKWISE)
left_motor = Motor(Port.F, Direction.CLOCKWISE)

# Initialize variables.
turn_error_ = 0
turn_tolerance = 1

async def Smart_Turn(turn_angle):
    global turn_error
    await wait(0)
    drive_base.settings(turn_rate=70)
    drive_base.settings(turn_acceleration=140)
    # Reset
    drive_base.reset(0, 0)
    prime_hub.imu.reset_heading(0)
    drive_base.use_gyro(True)
    # Main Turn
    await drive_base.turn(turn_angle)
    await wait(200)
    print('BEFORE', drive_base.angle())
    # Correction
    turn_error = turn_angle - drive_base.angle()
    if abs(turn_error) >= turn_tolerance:
        print('CORRECTION', turn_error)
        await drive_base.turn(turn_error)
        await wait(100)

async def subtask():
    # 540
    await drive_base.straight(260)
    await Smart_Turn(-90)
    drive_base.use_gyro(True)
    await drive_base.straight(550, then=Stop.BRAKE)
    drive_base.use_gyro(False)

async def main():
    await multitask(
        left_motor.run_until_stalled(-100, Stop.COAST, 20),
    )
    left_motor.reset_angle(0)
    right_motor.reset_angle(0)
    drive_base.reset(0, 0)
    drive_base.use_gyro(False)
    drive_base.settings(turn_rate=100)
    # test 310
    await multitask(
        subtask(),
        right_motor.run_target(200, 460),
    )
    # -39
    await drive_base.turn(-45)
    drive_base.settings(straight_speed=140)
    # 110
    await drive_base.straight(176)
    await drive_base.straight(-40)
    await right_motor.run_angle(300, -360)
    await drive_base.straight(40)
    await drive_base.straight(-200)
    # mission 4
    drive_base.settings(turn_rate=100)
    await drive_base.turn(106, then=Stop.BRAKE)
    await left_motor.run_target(500, 580)
    await drive_base.straight(100)
    await left_motor.run_target(600, -85)
    await left_motor.run_target(600, -70)
    # Return
    drive_base.settings(straight_speed=400)
    await drive_base.straight(-400)
    await drive_base.turn(120, then=Stop.BRAKE)
    await drive_base.straight(250, then=Stop.NONE)


run_task(main())