from pybricks.hubs import PrimeHub
from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase

# Set up all devices.
prime_hub = PrimeHub()
left = Motor(Port.A, Direction.COUNTERCLOCKWISE)
right = Motor(Port.E, Direction.CLOCKWISE)
drive_base = DriveBase(left, right, 87, 100)
right_motor = Motor(Port.B, Direction.COUNTERCLOCKWISE)
left_motor = Motor(Port.F, Direction.CLOCKWISE)


# The main program starts here.
drive_base.settings(turn_rate=100)
drive_base.turn(100)
left_motor.run_target(500, 750)
drive_base.straight(67)
left_motor.run_target(500, -90)
