from pybricks.hubs import PrimeHub
from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import multitask, run_task, wait

# Set up all devices.
prime_hub = PrimeHub()
left_drive = Motor(Port.A, Direction.COUNTERCLOCKWISE)
right_drive = Motor(Port.E, Direction.CLOCKWISE)
drive_base = DriveBase(left_drive, right_drive, 87, 112)
left_attachment = Motor(Port.F, Direction.CLOCKWISE)
right_attachment = Motor(Port.B, Direction.CLOCKWISE)

# Initialize variables.
AVG = 0
turn_error = 0
turn_tolerance = 1

async def Smart_Turn(turn_angle):
    global turn_error
    await wait(0)
    drive_base.settings(turn_rate=70)
    drive_base.settings(turn_acceleration=140)
    # Reset
    drive_base.reset(0, 0)
    prime_hub.imu.reset_heading(0)
    drive_base.use_gyro(True)
    # Main Turn
    await drive_base.turn(turn_angle)
    await wait(200)
    print('BEFORE', drive_base.angle())
    # Correction
    turn_error = turn_angle - drive_base.angle()
    if abs(turn_error) >= turn_tolerance:
        print('CORRECTION', turn_error)
        await drive_base.turn(turn_error)
        await wait(100)

async def main():
    for count in range(100):
        await wait(0)
        print('.....', '')
    # Settings for Drive Base
    drive_base.use_gyro(True)
    await drive_base.straight(-50)
    drive_base.settings(straight_speed=350)
    await drive_base.straight(740, then=Stop.BRAKE)
    await drive_base.turn(-50, then=Stop.BRAKE)
    await drive_base.turn(-10, then=Stop.BRAKE)
    await wait(200)
    await drive_base.straight(-100, then=Stop.BRAKE)
    await wait(400)
    await drive_base.turn(40, then=Stop.BRAKE)
    await wait(400)
    await drive_base.straight(38, then=Stop.BRAKE)
    await wait(400)
    await right_attachment.run_angle(250, 315, Stop.BRAKE)
    await wait(600)
    await drive_base.turn(40, then=Stop.BRAKE)
    await wait(400)
    await right_attachment.run_angle(250, -315, Stop.BRAKE)
    await wait(200)
    await drive_base.straight(-22, then=Stop.BRAKE)
    await drive_base.turn(-50, then=Stop.BRAKE)
    await wait(200)
    await drive_base.straight(-750)


run_task(main())