from pybricks.hubs import InventorHub
from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase

# Set up all devices.
prime_hub = InventorHub()
right_motor = Motor(Port.B, Direction.COUNTERCLOCKWISE)
left_motor = Motor(Port.F, Direction.CLOCKWISE)
left_2 = Motor(Port.A, Direction.COUNTERCLOCKWISE)
right_2 = Motor(Port.E, Direction.CLOCKWISE)
drive_base = DriveBase(left_2, right_2, 87, 100)


# The main program starts here.
drive_base.use_gyro(False)
drive_base.straight(775)
left_motor.control.limits(acceleration=4000)
left_motor.run_angle(1000, 6000)
drive_base.turn(43)
drive_base.straight(200)
drive_base.turn(-65)
drive_base.straight(1000)
